public static int Hash(string txt, int leftPointer, int substringLength, int con)
{


            int hash = 0;
            int iter = 0;

            while(iter < substringLength)
            {
                hash += (int)txt[leftPointer + iter] * (int)Math.Pow(con, (substringLength - 1) - iter);
                iter++;
            }

            return hash;

 }



public static bool RKM(string pattern, string txt)

 {

            int patternHash = Hash(pattern, 0, pattern.Length, 127);


            for(int txtIter = 0; txtIter < txt.Length - pattern.Length + 1; txtIter++)
            {

                string subString = txt.Substring(txtIter, pattern.Length);
                int subStringHash = Hash(txt, txtIter, pattern.Length, 127);

                if (patternHash == subStringHash && subString.Contains(pattern))
                    return true;


            }

            return false;

}
